#!/bin/sh
echo Ejecutar contenedor con servicio FTP-alpine
docker run --rm -d --name ftpNode --network testNetwork --ip 192.168.10.6 -p 8021:8021 -p 21000-21010:21000-21010 -v c:/testingEnvironmentBlog:/home/testingEnvironmentBlog -e USERS="ftpUser|ftp1234|/home/testingEnvironmentBlog|1000" -e ADDRESS="192.168.0.100" vsftpdalpine3.15:1.0
echo Ejecutar contenedores Wildfly
docker run --rm --name dockerNode1 --network testNetwork --ip 192.168.10.2 -v c:/testingEnvironmentBlog/standalonedockernode1:/opt/jboss/wildfly/standalone -v c:/testingEnvironmentBlog/scriptsServicioWildfly/levantardockernode1.sh:/opt/jboss/wildfly/levantarWildfly.sh -v c:/testingEnvironmentBlog/cacerts:/opt/java/openjdk/lib/security/cacerts --publish 8087:8087 --publish 9997:9997 -d alpine3.14wildfly26.1.1jdk17:1.0 
docker run --rm --name dockerNode2 --network testNetwork --ip 192.168.10.3 -v c:/testingEnvironmentBlog/standalonedockernode2:/opt/jboss/wildfly/standalone -v c:/testingEnvironmentBlog/scriptsServicioWildfly/levantardockernode2.sh:/opt/jboss/wildfly/levantarWildfly.sh -v c:/testingEnvironmentBlog/cacerts:/opt/java/openjdk/lib/security/cacerts --publish 8187:8187 --publish 10097:10097 -d alpine3.14wildfly26.1.1jdk17:1.0 
